import React, { useState } from 'react';

const brands = [
    { id: 1, name: 'BMW' },
    { id: 2, name: 'Mercedes' },
    { id: 3, name: 'Audi' },
];
const types = [
    { id: 1, name: 'Sedan' },
    { id: 2, name: 'SUV' },
    { id: 3, name: 'Coupe' },
];

function Home() {
    const [selectedBrand, setSelectedBrand] = useState(null);
    const [isClicked, SetIsClicked] = useState(false);
    const [selectedType, setSelectedType] = useState(null);
    const [match, setMatch] = useState(null);

    const handleBrandClick = (brand) => {
        setSelectedBrand(brand);
        setMatch(null);
    };

    const Clicked = () =>{
        setClicked(true);
    }
    const handleTypeClick = (type) => {
        setSelectedType(type);
        if (selectedBrand) {
            setMatch(selectedBrand.id === type.id);
        }
    };

    return (
        <div className="container w-screen h-screen inline-block p-6 bg-gradient-to-br from-green-400/10 to-blue-400/10 mx-auto justify-center items-center text-center">
            <h2 className="mt-5 mb-10">Brands</h2>
            <div className="container flex  justify-around border-b-2">
                {brands.map((brand) => (
                    <button
                        className = {{
                            backgroundColor:
                                selectedBrand === brand && match !== null
                                    ? match
                                        ? 'green'
                                        : 'red'
                                    : 'transparent',
                        }}
                        key={brand.id}
                        onClick={() => handleBrandClick(brand)}
                        style={{
                            backgroundColor:
                                selectedBrand === brand && match !== null
                                    ? match
                                        ? 'green'
                                        : 'red'
                                    : 'transparent',
                        }}
                    >
                        {brand.name}
                    </button>
                ))}
            </div>
            <h2 className="mt-10">Types</h2>
            <div className="container flex  justify-around mt-10">
                {types.map((type) => (
                    <button
                        key={type.id}
                        className="border p-3 px-5 rounded-xl"
                        onClick={() => handleTypeClick(type)}
                        style={{
                            backgroundColor:
                                selectedType === type && match !== null
                                    ? match
                                        ? 'green'
                                        : 'red'
                                    : 'transparent',
                        }}
                    >
                        {type.name}
                    </button>
                ))}
            </div>
            <svg xmlns="http://www.w3.org/2000/svg" width={16} height={16} fill={'red'}
                 className="bi bi-pentagon" viewBox="0 0 16 16"
                 data-darkreader-inline-fill="">
                <path
                    d="M7.685 1.545a.5.5 0 0 1 .63 0l6.263 5.088a.5.5 0 0 1 .161.539l-2.362 7.479a.5.5 0 0 1-.476.349H4.099a.5.5 0 0 1-.476-.35L1.26 7.173a.5.5 0 0 1 .161-.54l6.263-5.087Zm8.213 5.28a.5.5 0 0 0-.162-.54L8.316.257a.5.5 0 0 0-.631 0L.264 6.286a.5.5 0 0 0-.162.538l2.788 8.827a.5.5 0 0 0 .476.349h9.268a.5.5 0 0 0 .476-.35l2.788-8.826Z"></path>
            </svg>
        </div>
    );
}

export default Home;
